error.pattern:
                    //ExprStmt
	[Expr::BinaryExpr	[Left::IndexExpr
			[isPointer()]
			[Expr::IdExpr     
				[$array_name:=getName()]
                    		[$0 := getName()]
          		]
          		[Index::LiteralExpr     [$array_index:=getIntValue()]
                         	[$1 := getIntValue()]
          		]
		]
		[	Right::IdExpr
				[getName() = 'NULL']
			|
			Right::LiteralExpr
				[getIntValue() = 0]
		]
		[$2 := getNodeLine()]
	]
	/following-sibling::AnyStmt/descendant-or-self::CallExpr
		[getName()='memset']
		[Args[0]::IndexExpr
          		[Expr::IdExpr
				[$array_name = getName()]
        		]
          		[Index::LiteralExpr
				[$array_index = getIntValue()]
          		]
     		]
